import React, { useState, useEffect } from 'react';

const NeuralChatAuth = () => {
  const [activeTab, setActiveTab] = useState('login');
  const [showPassword, setShowPassword] = useState({
    login: false,
    signup: false,
    confirm: false
  });
  const [loading, setLoading] = useState({
    login: false,
    signup: false
  });
  const [notification, setNotification] = useState({
    show: false,
    message: '',
    type: 'success'
  });

  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
    rememberMe: false,
    agreeTerms: false
  });

  const showNotification = (message, type = 'success') => {
    setNotification({ show: true, message, type });
    setTimeout(() => {
      setNotification(prev => ({ ...prev, show: false }));
    }, 4000);
  };

  const togglePassword = (field) => {
    setShowPassword(prev => ({
      ...prev,
      [field]: !prev[field]
    }));
  };

  const handleInputChange = (field, value) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleLogin = async () => {
    setLoading(prev => ({ ...prev, login: true }));

    setTimeout(() => {
      setLoading(prev => ({ ...prev, login: false }));
      showNotification('This is a demo login page for NeuralChat AI. In a real application, this would authenticate your credentials and redirect you to the AI dashboard.', 'error');
    }, 2000);
  };

  const handleSignup = async () => {
    setLoading(prev => ({ ...prev, signup: true }));

    setTimeout(() => {
      setLoading(prev => ({ ...prev, signup: false }));
      showNotification('This is a demo signup page for NeuralChat AI. In a real application, this would create your account and set up your AI assistant.', 'error');
    }, 2500);
  };

  const handleSocialLogin = (provider) => {
    showNotification(`This is a demo page. In a real NeuralChat AI application, this would connect to ${provider} for quick authentication.`, 'error');
  };

  const aiFeatures = [
    {
      icon: 'fas fa-comments',
      title: 'Intelligent Conversations',
      description: 'Natural language processing with context awareness',
      gradient: 'from-blue-500 to-purple-600'
    },
    {
      icon: 'fas fa-code',
      title: 'Code Generation',
      description: 'Write, debug, and optimize code in any language',
      gradient: 'from-green-500 to-blue-500'
    },
    {
      icon: 'fas fa-lightbulb',
      title: 'Creative Solutions',
      description: 'Innovative ideas and creative problem solving',
      gradient: 'from-purple-500 to-pink-500'
    },
    {
      icon: 'fas fa-shield-alt',
      title: 'Secure & Private',
      description: 'End-to-end encryption and data protection',
      gradient: 'from-orange-500 to-red-500'
    }
  ];

  return (
    <div className="min-h-screen flex items-center justify-center p-4 relative" style={{
      background: 'linear-gradient(-45deg, #0f0f23, #1a1a2e, #16213e, #0f3460, #533483, #7209b7)',
      backgroundSize: '400% 400%',
      animation: 'gradientShift 20s ease infinite'
    }}>
      <style jsx>{`
        @import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800&display=swap');
        @import url('https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.0.0/css/all.min.css');

        * {
          font-family: 'Inter', sans-serif;
        }

        @keyframes gradientShift {
          0% { background-position: 0% 50%; }
          50% { background-position: 100% 50%; }
          100% { background-position: 0% 50%; }
        }

        @keyframes float {
          0% {
            transform: translateY(100vh) rotate(0deg);
            opacity: 0;
          }
          10% {
            opacity: 0.6;
          }
          90% {
            opacity: 0.6;
          }
          100% {
            transform: translateY(-100px) rotate(360deg);
            opacity: 0;
          }
        }

        @keyframes pulse {
          0%, 100% { transform: scale(1); }
          50% { transform: scale(1.05); }
        }

        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }

        @keyframes slideInRight {
          from {
            opacity: 0;
            transform: translateX(50px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }

        .floating-shape {
          position: absolute;
          background: rgba(114, 9, 183, 0.1);
          border-radius: 50%;
          animation: float 25s infinite linear;
        }

        .floating-shape:nth-child(1) {
          width: 120px;
          height: 120px;
          left: 10%;
          animation-delay: 0s;
        }

        .floating-shape:nth-child(2) {
          width: 80px;
          height: 80px;
          left: 70%;
          animation-delay: 8s;
        }

        .floating-shape:nth-child(3) {
          width: 100px;
          height: 100px;
          left: 50%;
          animation-delay: 15s;
        }

        .glass-morphism {
          background: rgba(255, 255, 255, 0.05);
          backdrop-filter: blur(20px);
          border: 1px solid rgba(255, 255, 255, 0.1);
          box-shadow: 0 25px 45px rgba(0, 0, 0, 0.2);
        }

        .input-field {
          background: rgba(255, 255, 255, 0.05);
          border: 2px solid rgba(255, 255, 255, 0.1);
          transition: all 0.3s ease;
        }

        .input-field:focus {
          background: rgba(255, 255, 255, 0.1);
          border-color: #7209b7;
          transform: translateY(-2px);
          box-shadow: 0 10px 25px rgba(114, 9, 183, 0.3);
        }

        .btn-primary {
          background: linear-gradient(135deg, #7209b7, #533483);
          transition: all 0.3s ease;
          position: relative;
          overflow: hidden;
        }

        .btn-primary::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
          transition: left 0.5s;
        }

        .btn-primary:hover::before {
          left: 100%;
        }

        .btn-primary:hover {
          transform: translateY(-3px);
          box-shadow: 0 15px 35px rgba(114, 9, 183, 0.4);
        }

        .social-btn {
          background: rgba(255, 255, 255, 0.05);
          border: 1px solid rgba(255, 255, 255, 0.1);
          transition: all 0.3s ease;
        }

        .social-btn:hover {
          background: rgba(255, 255, 255, 0.1);
          transform: translateY(-2px);
          box-shadow: 0 10px 25px rgba(114, 9, 183, 0.2);
        }

        .tab-active {
          background: rgba(114, 9, 183, 0.3);
          box-shadow: 0 5px 15px rgba(114, 9, 183, 0.2);
        }

        .ai-feature {
