                  </div>

                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      <i className="fas fa-envelope mr-2"></i>Email Address
                    </label>
                    <input 
                      type="email"
                      className="input-field w-full px-4 py-4 rounded-xl text-white placeholder-white placeholder-opacity-60 focus:outline-none"
                      placeholder="Enter your email"
                      value={formData.email}
                      onChange={(e) => handleInputChange('email', e.target.value)}
                    />
                  </div>

                  <div>
                    <label className="block text-white text-sm font-medium mb-2">
                      <i className="fas fa-lock mr-2"></i>Password
                    </label>
                    <div className="relative">
                      <input 
                        type={showPassword.signup ? 'text' : 'password'}
                        className="input-field w-full px-4 py-4 pr-12 rounded-xl text-white placeholder-white placeholder-opacity-60 focus:outline-none"
