import './App.css';
import { BrowserRouter as Router, Routes, Route, Navigate, useLocation } from 'react-router-dom';
import { AnimatePresence } from 'framer-motion';
import NeuralChatAuth from './pages/login-signup';
import NeuralChatLanding from './pages/landingPage';
import HomePage from './pages/homePage';
import PageTransition from './components/PageTransition';
import { useEffect } from 'react';

// Wrap routes with AnimatePresence for exit animations
const AnimatedRoutes = () => {
  const location = useLocation();

  // Apply global styles for larger text and elements
  useEffect(() => {
    // Create a style element
    const style = document.createElement('style');
    style.textContent = `
      :root {
        font-size: 18px; /* Increase base font size */
      }
      body {
        font-size: 1.2rem; /* Make body text larger */
      }
      button, input, select, textarea {
        font-size: 1.1rem; /* Larger form elements */
      }
      h1, h2, h3, h4, h5, h6 {
        font-size: 1.3em; /* Larger headings */
      }
      .container, .section, .card {
        padding: 1.5rem; /* More padding for containers */
      }
    `;
    // Add the style element to the head
    document.head.appendChild(style);

    // Clean up when component unmounts
    return () => {
      document.head.removeChild(style);
    };
  }, []);

  return (
    <AnimatePresence mode="wait">
      <Routes location={location} key={location.pathname}>
        <Route path="/" element={
          <PageTransition>
            <NeuralChatLanding />
          </PageTransition>
        } />
        <Route path="/auth" element={
          <PageTransition>
            <NeuralChatAuth />
          </PageTransition>
        } />
        <Route path="/home" element={
          <PageTransition>
            <HomePage />
          </PageTransition>
        } />
        <Route path="*" element={<Navigate to="/" />} />
      </Routes>
    </AnimatePresence>
  );
};

function App() {
  return (  
    <Router>
      <AnimatedRoutes />
    </Router>
  );
}

export default App;
