import React, { useState, useEffect, useRef } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faSignInAlt, faUserPlus, faEnvelope, faLock, faQuestionCircle, 
  faRocket, faEye, faEyeSlash, faUser, faBriefcase, faBrain,
  faRobot, faComments, faCode, faLightbulb, faShieldAlt
} from '@fortawesome/free-solid-svg-icons';
import { faGoogle, faGithub, faMicrosoft } from '@fortawesome/free-brands-svg-icons';
import '../styles/login-signup.css';

const LoginSignup = () => {
    // State variables
    const [activeTab, setActiveTab] = useState('login');
    const [showPassword, setShowPassword] = useState({
        login: false,
        signup: false
    });
    const [notification, setNotification] = useState({
        message: '',
        type: '',
        show: false
    });
    const [loading, setLoading] = useState({
        login: false,
        signup: false
    });

    // Refs for animation
    const loginFormRef = useRef(null);
    const signupFormRef = useRef(null);
    const emailInputRef = useRef(null);

    // Auto-focus on email input when component mounts
    useEffect(() => {
        if (emailInputRef.current) {
            emailInputRef.current.focus();
        }
    }, []);

    // Handle form switching with animation
    const switchTab = (tab) => {
        if (tab === activeTab) return;

        if (tab === 'login') {
            if (signupFormRef.current) signupFormRef.current.classList.add('form-slide-exit');
            setTimeout(() => {
                setActiveTab('login');
                setTimeout(() => {
                    if (loginFormRef.current) loginFormRef.current.classList.add('form-slide-enter');
                    setTimeout(() => {
                        if (loginFormRef.current) loginFormRef.current.classList.remove('form-slide-enter');
                    }, 500);
                }, 50);
            }, 250);
        } else {
            if (loginFormRef.current) loginFormRef.current.classList.add('form-slide-exit');
            setTimeout(() => {
                setActiveTab('signup');
                setTimeout(() => {
                    if (signupFormRef.current) signupFormRef.current.classList.add('form-slide-enter');
                    setTimeout(() => {
                        if (signupFormRef.current) signupFormRef.current.classList.remove('form-slide-enter');
                    }, 500);
                }, 50);
            }, 250);
        }
    };

    // Toggle password visibility
    const togglePassword = (formType) => {
        setShowPassword(prev => ({
            ...prev,
            [formType]: !prev[formType]
        }));
    };

    // Show notification
    const showNotificationMessage = (message, type = 'success') => {
        setNotification({ message, type, show: true });

        setTimeout(() => {
            setNotification(prev => ({ ...prev, show: false }));
        }, 4000);
    };

    // Form handlers
    const handleLogin = () => {
        setLoading({ ...loading, login: true });

        setTimeout(() => {
            setLoading({ ...loading, login: false });
            showNotificationMessage('This is a demo login page for NeuralChat AI. In a real application, this would authenticate your credentials and redirect you to the AI dashboard.', 'error');
        }, 2000);
    };

    const handleSignup = () => {
        setLoading({ ...loading, signup: true });

        setTimeout(() => {
            setLoading({ ...loading, signup: false });
            showNotificationMessage('This is a demo signup page for NeuralChat AI. In a real application, this would create your account and set up your AI assistant.', 'error');
        }, 2500);
    };

    const handleSocialLogin = (provider) => {
        showNotificationMessage(`This is a demo page. In a real NeuralChat AI application, this would connect to ${provider} for quick authentication.`, 'error');
    };

    // Input focus effect
    const handleInputFocus = (e) => {
        if (e.target.parentElement) {
            e.target.parentElement.style.transform = 'scale(1.02)';
        }
    };

    const handleInputBlur = (e) => {
        if (e.target.parentElement) {
            e.target.parentElement.style.transform = 'scale(1)';
        }
    };

    return (
        <div className="animated-bg min-h-screen flex items-center justify-center p-4 relative">
            {/* Floating Background Shapes */}
            <div className="floating-shapes">
                <div className="shape"></div>
                <div className="shape"></div>
                <div className="shape"></div>
            </div>

            {/* Neural Network Background */}
            <div className="neural-network">
                <svg width="100%" height="100%" viewBox="0 0 1000 1000">
                    <defs>
                        <pattern id="grid" width="50" height="50" patternUnits="userSpaceOnUse">
                            <path d="M 50 0 L 0 0 0 50" fill="none" stroke="rgba(114, 9, 183, 0.1)" strokeWidth="1"/>
                        </pattern>
                    </defs>
                    <rect width="100%" height="100%" fill="url(#grid)" />
                </svg>
            </div>

            {/* Notification Container */}
            <div className={`notification ${notification.type} ${notification.show ? 'show' : ''}`}>
